(58, 156, 'Merveilleuses pastèques', 'merveilleuses-pasteques', '<p>La pastèque est un véritable festin, particulièrement sain.</p><p style="text-align: justify;">Nous produisons les pastèques les plus suaves au monde et avons la plus grande variété de pastèques de tout le pays.</p>', '', 1, 30, '2012-01-01 00:00:01', 123, 'Joomla', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-01-01 00:00:01', '0000-00-00 00:00:00', '', '', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_readmore":"","show_print_icon":"","show_email_icon":"","show_hits":"","page_title":"","alternative_readmore":"","layout":""}', 1, 1, '', '', 1, 0, '', 0, '*', ''),
(59, 157, 'Module Fenêtre intégrée', 'module-fenetre-integree', '<p style="text-align: justify;">Le module «Fenêtre intégrée» (mod_wrapper) permet d''afficher une page d''un autre site dans une fenêtre intégrée (Iframe ou Wrapper). <a href="http://help.joomla.org/proxy/index.php?option=com_help&amp;keyref=Help31:Extensions_Module_Manager_Wrapper" title="Wrapper Module" target="_blank">Aide</a></p><div class="sample-module">{loadmodule wrapper,Fenêtre intégrée}</div>', '', 1, 67, '2012-01-01 00:00:01', 123, 'Joomla', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-01-01 00:00:01', '0000-00-00 00:00:00', '', '', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_readmore":"","show_print_icon":"","show_email_icon":"","show_hits":"","page_title":"","alternative_readmore":"","layout":""}', 1, 1, '', '', 1, 0, '', 0, '*', ''),
(60, 158, 'Composant Fils d''actualité', 'composant-fils-actualite', '<p style="text-align: justify;">Le composant «Fils d''actualité» (com_newsfeeds) permet d''organiser et présenter sur votre site des informations provenant d''autres sites par le biais de flux RSS, RDF ou ATOM. Par exemple, les sites joomla.org et joomla.fr proposent de nombreux flux que vous pouvez afficher sur votre site. Vous pouvez utiliser les menus pour afficher un fil d''actualité unique, une liste de fils d''actualité d''une catégorie, ou la liste de toutes les catégories de fils d''actualité. <a href="http://help.joomla.org/proxy/index.php?option=com_help&amp;keyref=Help31:Components_Newsfeeds_Feeds" target="_blank">Aide</a></p>', '', 1, 21, '2012-01-01 00:00:01', 123, 'Joomla', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-01-01 00:00:01', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":"","urlatext":"","targeta":"","urlb":"","urlbtext":"","targetb":"","urlc":"","urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 1, 4, '', '', 1, 0, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
